.align 4
@ R1 - char code R2 - gl parameters pointer; OUT R1 - pointer to buffer
GetMonoBuffer:
     push {r0, r2-r7, lr}
          EOR R5, R5
          SUBS R1, #32
          BMI GetMonoBuffer_Return
          CMP R1, FONT_SYSTEM_10_LENGTH
          BPL GetMonoBuffer_Return
          LSL R4, R1, #5                 @ char * 32
          ADD R4, R1                    @ char * 33 (shift to char row)
          ADD R4, R1                    @ char * 34 (shift to char row)
          LDR R1, =font16x16_sys
          ADD R1, #20                    @ start of table
          ADD R3, R4, R1                @ R3 - pointer to char row, data starts from +1 index
          ADD R3, #2
          MOV R0, #256                   @ 16*16 field
          MOV R1, #1                     @ hword data type
          BL GetArray
          EOR R0, R0                    @ hword shift counter
          EOR R6, R6                    @ color buffer shift counter
          EOR R7, R7                    @ bit counter
          LDR R4, [R2, #4]               @ R4 - foreground color
          LDR R2, [R2]                  @ R2 - background color

GetMonoBuffer_rowLoop:
          LDR R1, [R3, R0]              @ load 2 char data rows
          @@ROR R1, R1, #16
     GetMonoBuffer_colLoop:
          LSRS R1, R1, #1                @ shift char data rows left
          ITE CS
               STRHCS r4, [r5, r6]      @ if bit is 1 - write foreground color to buffer
               STRHCC r2, [r5, r6]      @ else write backgroung
          ADD r6, #2                     @ shift buffer pointer to 2
          ADD r7, #1                     @ count bits
          CMP r7, #32
          BNE GetMonoBuffer_colLoop
          EOR R7, R7                    @ erase counter
          ADD R0, #4                     @ shift data pointer to next hword pare
          CMP R0, #32
          BNE GetMonoBuffer_rowLoop
GetMonoBuffer_Return:
     MOV R1, R5
     pop {r0, r2-r7, lr}
     bx lr

@ R1 - char code R2 - gl parameters pointer; OUT R1 - pointer to buffer R2 - actual width
GetProportionalBuffer:
     push {r0, r3-r8, lr}
          EOR R5, R5
          EOR R8, R8
          SUBS R1, #32
          BMI GetProportionalBuffer_Return
          LSL R4, R1, #5                 @ char * 32

          ADD R4, R1                    @ char * 33 (shift to char row)
          ADD R4, R1                    @ char * 34 (shift to char row)
          LDR R1, =font16x16_sys
          ADD R1, #20                   @ start of table
          ADD R3, R4, R1                @ R3 - pointer to char row, data starts from +1 index
          LDRH R8, [r3]                 @ bits counter

          MOV R0, #16
          ADD R8, R8, #1
          MUL R0, R8                    @ (charDataWidth+1)*charHeight - buffer size with 1 spase bit
          MOV R1, #1
          BL GetArray

          LDR R4, [R2, #4]               @ R4 - foreground color
          LDR R2, [R2]                  @ R2 - background color

          MOV R0, #2                     @ hword shift counter
          EOR R6, R6                    @ color buffer shift counter
GetProportionalBuffer_ROWS_LOOP:
          SUB R7, R8, #1                    @ bits counter
          LDRH R1, [r3,r0]              @ get curent hword
     GetProportionalBuffer_COLS_LOOP:
          LSRS R1, R1, #1                @ shift char data rows left
          ITE CS
               STRHCS r4, [r5, r6]       @ if bit is 1 - write foreground color to buffer
               STRHCC r2, [r5, r6]       @ else write backgroung
          ADD r6, #2                     @ shift buffer pointer to 2
          SUBS r7, #1                     @ count bits
          BNE GetProportionalBuffer_COLS_LOOP
          STRH r2, [r5, r6]
          ADD r6, #2
          ADD R0, #2
          CMP R0, #34
          BNE GetProportionalBuffer_ROWS_LOOP

GetProportionalBuffer_Return:
     MOV R1, R5
     MOV R2, R8
     pop {r0, r3-r8, lr}
     bx lr

@R1 - char code, OUT R2 - char width
GetCharWidth:
     push {r1,r3,r4,lr}
     EOR R2, R2
     SUBS R1, 32
     BMI GetCharWidthExit
     LSL R4, R1, #5                @ char * 32
     ADD R4, R1                    @ char * 33 (shift to char row)
     ADD R4, R1                    @ char * 34 (shift to char row)
     LDR R1, =font16x16_sys
     LDRH R2, [R1]
     LDRH R3, [R1, 6]              @ get type
     cbz r3, GetCharWidthExit
     ADD R1, #20                   @ start of table
     ADD R3, R4, R1                @ R3 - pointer to char row, data starts from +1 index
     LDRH R2, [r3]
     add r2, 1
GetCharWidthExit:
     pop {r1,r3,r4,lr}
     bx lr


